INSERT INTO filmes (hash, name, folder_id, hls_id, poster, backdrop, year, categories, duration, description) VALUES ("bd427f1b-d64c-4ed5-bdb4-01a170c2382a", "Paulo, Apóstolo de Cristo", "1642E726B682B518!166421", "1642E726B682B518!166642", "/5DPz4InO3UUY94l2PT1jH6PsPFe.jpg", NULL, "2018", "Drama", "1h 48m", "Paulo (James Faulkner) era conhecido como um dos perseguidores de cristãos mais cruel de seu tempo. Mas tudo muda quando ele tem um encontro com o próprio Jesus. A partir desse momento, esse jovem se torna um dos apóstolos mais influentes do Cristianismo.");
INSERT INTO filmes (hash, name, folder_id, hls_id, poster, backdrop, year, categories, duration, description) VALUES ("57f328fc-1142-44a2-a866-0b481d869b52", "Cruzada", "1642E726B682B518!166753", "1642E726B682B518!166839", "/2VXjb6myxAppgfu6YRF4YsVmjZo.jpg", NULL, "2005", "Drama, Ação, Aventura, História, Guerra", "2h 24m", "Ainda em luto pela repentina morte de sua esposa, o ferreiro Balian junta-se ao seu distante pai, Baron Godfrey, nas cruzadas a caminho de Jerusalém. Após uma jornada muito difícil até à cidade santa, o jovem valente entra no séquito do rei leproso Balduíno IV, que deseja lutar contra os muçulmanos para seu próprio ganho político e pessoal.");
